mapper.select:
select count(*) from RC_INTERVIEW,RC_RESUME
INTERVIEW_USER_ID = #{userId,jdbcType=INTEGER} and RC_INTERVIEW.INTERVIEW_RESUME_ID = RC_RESUME.RESUME_ID and RC_INTERVIEW.INTERVIEW_DELETE_FLAG = 0
AND RC_INTERVIEW.INTERVIEW_TIME >= #{startTime}
AND RC_INTERVIEW.INTERVIEW_TIME <= #{overTime}
AND RC_INTERVIEW.INTERVIEW_JOB LIKE '%${interviewJob}%'
AND RC_RESUME.RESUME_NAME LIKE '%${interviewInfo}%' OR RC_INTERVIEW.INTERVIEW_ADDRESS LIKE '%${interviewInfo}%' OR RC_INTERVIEW.INTERVIEW_INFO LIKE '%${interviewInfo}%'

mapper.update:
update RC_INTERVIEW 
set INTERVIEW_DELETE_FLAG = 1,
	INTERVIEW_UPDATE_TIME = sysdate,
	INTERVIEW_UPDATE_USER = #{interviewUpdateUser}
 where INTERVIEW_ID = #{interviewId,jdbcType=INTEGER}
